import Layout from "../../components/Layout";
import { withRouter } from "next/router";
import { useRouter } from "next/router";
import React, { useState } from "react";
// import { Button } from 'antd';
// import { useEffect } from 'react'
import "../css/newbookdetail.css";
import cssobj from "../css/newbookdetail2.less";
import Footer from "../components/footerbook";
import Aside from "../components/aside";
import Topboxnav from "../components/topboxnav";

import { Comment, Avatar, Form, Button, List, Input } from "antd";
import moment from "moment";

const { TextArea } = Input;

// const CommentList = ({ comments }) => (
//   <List
//     dataSource={comments}
//     header={`${comments.length} ${comments.length > 1 ? 'replies' : 'reply'}`}
//     itemLayout="horizontal"
//     renderItem={props => <Comment {...props} />}
//   />
// );

// const Editor = ({ onChange, onSubmit, submitting, value }) => (
//   <div>
//     <Form.Item>
//       <TextArea rows={4} onChange={onChange} value={value} />
//     </Form.Item>
//     <Form.Item>
//       <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
//         Add Comment
//       </Button>
//     </Form.Item>
//   </div>
// );

type IProps = {
  show: any;
};

// let bookidtest = 0;
export default class App extends React.Component<IProps> {
  static async getInitialProps(context: any) {
    // console.log(context.query)
    const { id } = context.query;
    // console.log(id)
    const res = await fetch(`http://localhost:3001/novel/${id}`);
    const data = await res.json();
    //这边返回的数据会被挂载到组件的props中
    // console.log(data);
    return { show: data };
  }

  // state = {
  //   comments: [],
  //   submitting: false,
  //   value: '',
  // };

  // handleSubmit = () => {
  //   if (!this.state.value) {
  //     return;
  //   }

  //   this.setState({
  //     submitting: true,
  //   });

  //   setTimeout(() => {
  //     this.setState({
  //       submitting: false,
  //       value: "",
  //       comments: [
  //         {
  //           author: "Han Solo",
  //           avatar:
  //             "https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png",
  //           content: <p>{this.state.value}</p>,
  //           datetime: moment().fromNow(),
  //         },
  //         ...this.state.comments,
  //       ],
  //     });
  //   }, 1000);
  // };

  handleChange = (e: any) => {
    this.setState({
      value: e.target.value,
    });
  };

  render() {
    // const { comments, submitting, value } = this.state;
    return (
      <Layout title="详情页">
        <div className="body-newbookbox">
          {/*----------头部导航栏开始--------------------*/}
          <Topboxnav></Topboxnav>
          {/* ----------头部导航栏结束---------------------*/}
          {/*------书本详情框开始-------------------------- */}
          <div className="bookdetail-ershou">
            <div className="bookdetailbox">
              <div className="detailbook">书本详情</div>
            </div>
          </div>
          {/*------书本详情框结束--------------------------   */}
          {/* --------------主体部分-------------------------- */}
          {this.props.show.map((item: any) => {
            return (
              <div className="detailbox-jiuwang" key={item.bookName}>
                <div id="wrapper">
                  <h1>
                    <span>{item.bookName}</span>
                    <div className="clear"></div>
                  </h1>
                  {/* --------------------*/}
                  <div id="content">
                    <div className="grid-16-8 clearfix">
                      <div className="article">
                        <div className="indent">
                          <div className="subjectwrap clearfix">
                            <div className="subject clearfix">
                              <div id="mainpic">
                                <a>
                                  <img
                                    src={`../static/images/image/${item.imgPath}`}
                                    className="mainpicimg"
