import Layout from "../components/Layout";
import { withRouter } from "next/router";
import React from "react";
import "@/static/css/font-awesome.css";
import "./css/index.css";
import fetch from "isomorphic-unfetch";
import { fetchpost, fetchget } from "../utils/zgfetch";
// import Fromdemo from "./components/from"
type State = {

}
export default class IndexPage extends React.Component <State>{
  static async getInitialProps() {
    const res = await fetch("http://localhost:3001/users");
    const show = await res.json();
    //这边返回的数据会被挂载到组件的props中
    return { show };
  }

  constructor(props: any) {
    super(props);
    this.state = {
      userName: "", //用户名
      passWord: "", //密码
      use: "",
      pwd: "",
      value1: "",
    };
  }

  search = (e: any) => {
    console.log(e.target.value);
    this.setState({ value1: e.target.value });
  };
  toValue = () => {
    console.log(this.state.value1);
    // this.props.reload(this.state.value1)
    // this.state.history.push('/search')
    // url = `http://localhost:3001/search/${this.state.value1}`
    IndexPage.getInitialProps();
    // this.setState({data:IndexPage.getInitialProps()})
    // data=IndexPage.getInitialProps();
  };

  render() {
    return (
      <Layout title="登录页">
        <div className="container clear">
          <div className="tbl">
            <div></div>
          </div>
          <div className="ph clear">
            {/* <%--显示手机图--%> */}
            {/* <div className="o"></div> */}
          </div>
          <div className="m clear">
            <div>密码登录</div>
            <div>
              <form className="loginBox" action="/home" method="post">
                <input name="userName" placeholder="请输入用户名" />
                <br></br>
                <input
                  //   type="password"
                  //   name="password"
                  placeholder="请输入密码"
                />
                <br></br>
                <input type="button" value="登录豆瓣读书" />
              </form>
              {/* <Fromdemo></Fromdemo> */}
              {/* <div></div> */}
            </div>
            {/* <%--二维码标识--%> */}
            <div></div>
            <div className="tx clear">---换一种登录方式---</div>
            <div className="wx"></div>
            <div className="xl clear"></div>
          </div>
          <div className="sew">
            <div></div>
            <div>登录孔子旧书APP</div>
          </div>
        </div>
      </Layout>
    );
  }
}
